import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { PencilAltIcon, TrashIcon } from "@heroicons/react/solid";
import { fetchPostDetailAction } from "../../redux/slices/posts/postSlices";
import { useDispatch, useSelector } from "react-redux";
import DateFormatter from "../../utils/DateFormatter";
import LoadingComponent from "../../utils/LoadingComponent";

const PostDetails = ({match: {params: {id}}}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchPostDetailAction(id));
  }, [id, dispatch]);

  //select post details from store
  const post = useSelector(state => state?.post);
  const {postDetails, loading, appErr, serverErr} = post;

  return (
    <>
      {loading ? <LoadingComponent/>: appErr}
    </>
  );
};

export default PostDetails;
