import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUsersAction } from "../../../redux/slices/users/userSlices";
import LoadingComponent from "../../../utils/LoadingComponent";
import UsersListHeader from "./UsersListHeader";
import UsersListItem from "./UsersListItem";

const UsersList = () => {
  //dispatch
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchUsersAction());
  },[]);

  return (
    <>
      <section class="py-8 bg-gray-900 min-h-screen">
        {loading ? <LoadingComponent /> : appErr || serverErr ? 
        <h3 className="text-yellow-600 text-center text-lg">
          {serverErr} {appErr}
        </h3> : usersList?.length <= 0 ? <h2>No user found!</h2> : usersList?.map(user => (
          <>
            <UsersListItem user={user}/>
          </>
        ))}
      </section>
    </>
  );
};

export default UsersList;
