import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import axios from 'axios';
import { baseUrl } from '../../../utils/baseURL';


//action
export const createCategoryAction = createAsyncThunk(
    'category/create',
    async (category, {rejectWithValue, getState, dispatch}) => {
        //get user token
        const user = getState()?.users;
        const {userAuth} = user;
        const config = {
            headers: {
                Authorization: `Bearer ${userAuth?.token}`,
            },
        }
        //http call
        try {
            const {data} = await axios.post(`${baseUrl}/api/category`, {
                title: category?.title,
            },
            config
            );
            return data;
        } catch (error) {
            if (!error?.response) {
                throw error;
            }
            return rejectWithValue(error?.response?.data);
        }
    }
);


//fetch
export const fetchCategoriesAction = createAsyncThunk(
    'category/fetch',
    async (category, {rejectWithValue, getState, dispatch}) => {
        //get user token
        const user = getState()?.users;
        const {userAuth} = user;
        const config = {
            headers: {
                Authorization: `Bearer ${userAuth?.token}`,
            },
        }
        //http call
        try {
            const {data} = await axios.get(`${baseUrl}/api/category`, config);
            return data;
        } catch (error) {
            if (!error?.response) {
                throw error;
            }
            return rejectWithValue(error?.response?.data);
        }
    }
);

//fetch details
export const fetchCategoryAction = createAsyncThunk(
    'category/details',
    async (category, {rejectWithValue, getState, dispatch}) => {
        //get user token
        const user = getState()?.users;
        const {userAuth} = user;
        const config = {
            headers: {
                Authorization: `Bearer ${userAuth?.token}`,
            },
        }
        //http call
        try {
            const {data} = await axios.get(`${baseUrl}/api/category/${id}`, config);
            return data;
        } catch (error) {
            if (!error?.response) {
                throw error;
            }
            return rejectWithValue(error?.response?.data);
        }
    }
);

//update
export const updateCategoriesAction = createAsyncThunk(
    'category/update',
    async (id, {rejectWithValue, getState, dispatch}) => {
        //get user token
        const user = getState()?.users;
        const {userAuth} = user;
        const config = {
            headers: {
                Authorization: `Bearer ${userAuth?.token}`,
            },
        }
        //http call
        try {
            const {data} = await axios.put(`${baseUrl}/api/category/${id}`, config);
            return data;
        } catch (error) {
            if (!error?.response) {
                throw error;
            }
            return rejectWithValue(error?.response?.data);
        }
    }
);

//delete
export const deleteCategoriesAction = createAsyncThunk(
    'category/delete',
    async (id, {rejectWithValue, getState, dispatch}) => {
        //get user token
        const user = getState()?.users;
        const {userAuth} = user;
        const config = {
            headers: {
                Authorization: `Bearer ${userAuth?.token}`,
            },
        }
        //http call
        try {
            const {data} = await axios.delete(`${baseUrl}/api/category/${id}`, config);
            return data;
        } catch (error) {
            if (!error?.response) {
                throw error;
            }
            return rejectWithValue(error?.response?.data);
        }
    }
);


//slices
const categorySlices = createSlice({
    name: 'category',
    initialState: {category: 'Node.js'},
    extraReducers: (builder)=> {
        //create
        builder.addCase(createCategoryAction.pending, (state, action) => {
            state.loading = true;
        });
        builder.addCase(createCategoryAction.fulfilled, (state, action)=> {
            state.category = action?.payload;
            state.loading = false;
            state.appErr = undefined;
            state.serverErr = undefined;
        });
        builder.addCase(createCategoryAction.rejected, (state, action)=> {
            state.loading = false;
            state.appErr = action?.payload?.message;
            state.serverErr = action?.error?.message;
        });
        //fetch all
        builder.addCase(fetchCategoriesAction.pending, (state, action) => {
            state.loading = true;
        });
        builder.addCase(fetchCategoriesAction.fulfilled, (state, action) => {
            state.categoryList = action?.payload;
            state.loading = false;
            state.appErr = undefined;
            state.serverErr = undefined;
        });
        builder.addCase(fetchCategoriesAction.rejected, (state, action) => {
            state.loading = false;
            state.appErr = action?.payload?.message;
            state.serverErr = action?.error?.message;
        });
        //update
        builder.addCase(updateCategoriesAction.pending, (state, action) => {
            state.loading = true;
        });
        builder.addCase(updateCategoriesAction.fulfilled, (state, action) => {
            state.updatedCategory = action?.payload;
            state.loading = false;
            state.appErr = undefined;
            state.serverErr = undefined;
        });
        builder.addCase(updateCategoriesAction.rejected, (state, action) => {
            state.loading = false;
            state.appErr = action?.payload?.message;
            state.serverErr = action?.error?.message;
        });
        //delete
        builder.addCase(deleteCategoriesAction.pending, (state, action) => {
            state.loading = true;
        });
        builder.addCase(deleteCategoriesAction.fulfilled, (state, action) => {
            state.deletedCategory = action?.payload;
            state.loading = false;
            state.appErr = undefined;
            state.serverErr = undefined;
        });
        builder.addCase(deleteCategoriesAction.rejected, (state, action) => {
            state.loading = false;
            state.appErr = action?.payload?.message;
            state.serverErr = action?.error?.message;
        });
        //fetch details
        builder.addCase(fet.pending, (state, action) => {
            state.loading = true;
        });
        builder.addCase(deleteCategoriesAction.fulfilled, (state, action) => {
            state.deletedCategory = action?.payload;
            state.loading = false;
            state.appErr = undefined;
            state.serverErr = undefined;
        });
        builder.addCase(deleteCategoriesAction.rejected, (state, action) => {
            state.loading = false;
            state.appErr = action?.payload?.message;
            state.serverErr = action?.error?.message;
        });
    }
});

export default categorySlices.reducer;
